&lt;script type=&quot;text/javascript&quot;&gt;
  $(document).ready(function() {

    //pageSetUp();

    var form_input = $(&quot;#form-input&quot;).validate({

      // Rules for form validation
      rules: {
        username: {
          required: true
        },
