                        $(&#039;#device_id&#039;).val(data.device_id || &#039;&#039;);
                        $(&#039;#referral_user_id&#039;).val(data.referral_user_id || 0);
                        $(&#039;#user_type_id&#039;).val(data.user_type_id || 2);
                        $(&#039;#user_note&#039;).val(data.user_note || &#039;&#039;);

                        // Checkboxes
                        $(&#039;#email_verified&#039;).prop(&#039;checked&#039;, data.email_verified == 1);
                        $(&#039;#is_send_mail&#039;).prop(&#039;checked&#039;, data.is_send_mail == 1);
                        $(&#039;#is_account_verified&#039;).prop(&#039;checked&#039;, data.is_account_verified == 1);

                        // Hide loading
                        $(&#039;#modalLoadingInfoEditPopup&#039;).hide();

                        console.log(&#039;Form populated:&#039;, {
                            user_id: $(&#039;#user_id&#039;).val(),
                            username: $(&#039;#username&#039;).val(),
                            name: $(&#039;#name&#039;).val()
                        });
                    }, 300); // Wait 300ms for modal animation
                },
                error: function(xhr, status, error) {
                    console.error(&#039;Ajax failed:&#039;, {
                        status: status,
                        error: error,
                        response: xhr.responseText
                    });

                    Swal.fire({
                        icon: &#039;error&#039;,
                        title: &#039;Error&#039;,
                        text: &#039;Failed to load user data: &#039; + error
                    });

                    $(&#039;#modal_panel_edit_popup&#039;).modal(&#039;hide&#039;);
                },
                complete: function() {
                    $(&#039;#modalLoadingInfoEditPopup&#039;).hide();
                }
            });
        }
    }

